func TestSymmetricEncrypt(t *testing.T) {
	bytes := GenSecKey()
	type args struct {
		stringToEncrypt string
		key             []byte
	}
	tests := []struct {
		name string
		args args
		want string
	}{
		{
			name: "shouldEncrypt",
			args: args{
				"{\"abc\":\"def\"}",
				bytes,
			},
			want: "bd4eea8dddcb650aa1f39329e09508c3c0831db54fe424d248bbb45201e2037c885bf3837c31f913db",
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got := ""
			if got = SymmetricEncrypt([]byte(tt.args.stringToEncrypt), tt.args.key); SymmetricDecrypt(got, tt.args.key) != tt.args.stringToEncrypt {
				t.Errorf("SymmetricEncrypt() = %v, want %v", got, tt.want)
			}
		})
	}
}

